import React from "react";
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function EditProfilePopup(props, isOpen, onClose) {

  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");

  const currentUser = React.useContext(CurrentUserContext);

// После загрузки текущего пользователя из API
// его данные будут использованы в управляемых компонентах.
  React.useEffect(() => {
  setName(currentUser.name);
  setDescription(currentUser.about);
}, [currentUser]); 

  // Обработчик изменения инпута обновляет стейт
  function handleChangeName(e) {
    setName(e.target.value);
  }

  function handleChangeDescription(e) {
    setDescription(e.target.value);
  }

  function handleSubmit(e) {
    // Запрещаем браузеру переходить по адресу формы
    e.preventDefault();

    // Передаём значения управляемых компонентов во внешний обработчик
    props.onUpdateUser({
      name,
      about: description,
    });
  } 


  return(
    <>
    <div name="profile-popup" title="Редактировать профиль" ionCli={isOpen} onClose={onClose} textButton="Cохранить">
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_name" id="popup__name" type="text" value={name} onChange={handleChangeName} placeholder="Имя" name="name" minLength="2" maxLength="40" required />
          <span className="popup__input-error popup__name-error"></span>
        </div>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_job" id="popup__text" type="text" value={description} onChange={handleChangeDescription} placeholder="О себе" name="about" minLength="2" maxLength="200" required />
          <span className="popup__input-error popup__text-error"></span>
        </div>
      </div>
    </>
  )
}

export default EditProfilePopup;
