import React from "react";
//import {CurrentUserContext} from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

function EditAvatarPopup(props) {

 const [avatar, setAvatar] = React.useState('');
 const avatarRef = React.useRef('');

 function handleChange(e) {
  setAvatar(e.target.value);
}

  function handleSubmit(e) {
    e.preventDefault();

    props.onUpdateAvatar({
      avatar: avatarRef.current
    })
  } 

  return(
    <PopupWithForm name="popup-new-avatar" title="Обновить аватар" textButton="Cохранить" isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit}>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_avatar" id="popup__newAvatar" type="url" name="link" value={avatar} onChange={handleChange} required />
          <span className="popup__input-error popup__newAvatar-error"></span>
        </div>
      </PopupWithForm>
  )
}

export default EditAvatarPopup;
