import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import api from "../utils/Api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import {CurrentCard} from "../contexts/CurrentCard";

function App() {
  const [isEditAvatarClick, setEditAvatarClick] = React.useState(false);
  const [isEditProfileClick, setEditProfileClick] = React.useState(false);
  const [isAddPlaceClick, setAddPlaceClick] = React.useState(false);
  const [selectedCard, setselectedCard] = React.useState(null);

  const [currentUser, setCurrentUser] = React.useState({});

  const [currentCard, setCurrentCard] = React.useState([]);

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    (isLiked
    ? api.deleteLikeCard(card._id)
    : api.addLikeCard(card._id))

    .then((newCard) => {
      setCurrentCard((state) => state.map((c) => (c._id === card._id ? newCard : c))
      );
    })
    .catch((err) => {
      console.log(err);
    })
}
function handleCardDelete(cardы) {
  const isOwn = cardы.owner._id === currentUser._id;

  api.deleteCard(card._id, !isOwn).then((card) => {
    setCurrentCard((state) => state.filter((c) => c._id === card._id ? card : c));
    // с - объект карточки
    // state - массив карточек до удаления 
});

}

  React.useEffect(() => {
    Promise.all([api.getInfoUser(), api.getInitialCards()])
      .then((res) => {
        setCurrentUser(res[0]);
        setCurrentCard([...res[1]]);
      })

      .catch((err) => {
        console.log(err);
      });
  }, []);

  const closeAllPopups = () => {
    setEditAvatarClick(false);
    setEditProfileClick(false);
    setAddPlaceClick(false);
    setselectedCard(null);
  };

  const handleEditAvatarClick = () => {
    return setEditAvatarClick(!isEditAvatarClick);
  };

  const handleEditProfileClick = () => {
    return setEditProfileClick(!isEditProfileClick);
  };

  const handleAddPlaceClick = () => {
    return setAddPlaceClick(!isAddPlaceClick);
  };

  const handleCardClick = (card) => {
    setselectedCard(card);
  };

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CurrentCard.Provider value={currentCard}>
    <div className="page">
      <Header />
      <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick} onCardClick={handleCardClick} onCardLike={handleCardLike} onCardDelete={handleCardDelete}/>
      <Footer />

      <PopupWithForm name="profile-popup" title="Редактировать профиль" isOpen={isEditProfileClick} onClose={closeAllPopups} textButton="Cохранить">
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_name" id="popup__name" type="text" defaultValue={""} placeholder="Имя" name="name" minLength="2" maxLength="40" required />
          <span className="popup__input-error popup__name-error"></span>
        </div>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_job" id="popup__text" type="text" defaultValue={""} placeholder="О себе" name="about" minLength="2" maxLength="200" required />
          <span className="popup__input-error popup__text-error"></span>
        </div>
      </PopupWithForm>

      <PopupWithForm name="popup-add" title="Новое место" textButton="Создать" isOpen={isAddPlaceClick} onClose={closeAllPopups}>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_title" id="popup__title" type="text" placeholder="Название" name="name" defaultValue={""} minLength="2" maxLength="30" required />
          <span className="popup__input-error popup__title-error"></span>
        </div>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_link" id="popup__link" type="url" placeholder="Ссылка на картинку" name="link" defaultValue={""} required />
          <span className="popup__input-error popup__link-error"></span>
        </div>
      </PopupWithForm>

      <PopupWithForm name="popup-new-avatar" title="Обновить аватар" textButton="Cохранить" isOpen={isEditAvatarClick} onClose={closeAllPopups}>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_avatar" id="popup__newAvatar" type="url" name="link" defaultValue={""} required />
          <span className="popup__input-error popup__newAvatar-error"></span>
        </div>
      </PopupWithForm>

      <ImagePopup card={selectedCard} onCardClick={handleCardClick} onClose={closeAllPopups} />
    </div>
    </CurrentCard.Provider>
    </CurrentUserContext.Provider>
  );
}

export default App;
