import React from 'react';
import api from '../utils/Api'

function Main ({onEditProfile, onAddPlace, onEditAvatar}) {
    const [userName, setUserName] = React.useState();
    const [userDescription, setUserDescription] = React.useState();
    const [userAvatar, setUserAvatar] = React.useState();

    const [cards, setCards] = React.useState([]);

    /*{let userId = null;}*/
    React.useEffect(() => {
      Promise.all([api.getInfoUser(), api.getInitialCards()])
      .then((res) => {
        setUserName(res[0].name);
        setUserDescription(res[0].about);
        setUserAvatar(res[0].avatar);
      })

      .then((cards) => {
        setCards(cards.map((card) => {
          return (
            <template id="cards">
                <div className="element">
                    <img src="#" className="element__image" alt={card.name} style={{ backgroundImage: `url(${card.link})` }} />
                    <div className="element__item">
                        <p className="element__title" name="name">{card.name}</p>
                        <button className="element__delete"></button>
                        <div className="element__likes-and-number">
                            <button className="element__like"></button>
                            <span className="element__like-number">{card.likes.length}</span>
                        </div>
                    </div>
                </div>
            </template>
          )
        }));
      })
      .catch((err) => {
        console.log(err); // выведем ошибку в консоль
        });
     })

    return(
    <main>
            <section className="profile">
                <div className="profile__avatar-container" style={{ backgroundImage: `url(${userAvatar})` }} onClick={onEditAvatar}>
                </div>
                <div className="profile__name">
                    <div className="profile__intro">
                        <h1 className="profile__intro-title">{userName}</h1>
                        <button className="profile__button-edit" type="button" onClick={onEditProfile}></button>
                    </div>
                    <p className="profile__intro-subtitle">{userDescription}</p>
                </div>

                <button className="profile__button-add"type="button" onClick={onAddPlace}></button>
            </section>

            <section className="elements">

            </section>
        </main>
  )
}

export default Main;
