import {CurrentUserContext} from "../contexts/CurrentUserContext";

function Card({ card, like, onCardClick, onCardLike }) {

  const currentUser = React.useContext(CurrentUserContext)
  function handleClick() {
    onCardClick(card);
  }

  function handleLikeClick() {
    onCardLike(card);
  }

  const isOwn = card.owner._id === card.currentUser;

  const isLiked = card.likes.some(i => i._id === card.currentUser);

  const cardLikeButtonClassName = ( 
    `element__like ${isLiked && 'element__like_active'}` 
  );

 // console.log(cardLikeButtonClassName)


  return (
    <div className="element">
      <img className="element__image" src={card.link} alt={card.name} onClick={handleClick} />
      <div className="element__item">
        <p className="element__title" name="name">
          {card.name}
        </p>
        {isOwn && <button className="element__delete"></button>}
        <div className="element__likes-and-number">
          <button className={cardLikeButtonClassName} onClick={handleLikeClick}></button>
          <p className="element__like-number">{like}</p>
        </div>
      </div>
    </div>
  );
}

export default Card;
