function Main () {


  document.querySelector('profile__avatar-container').addEventListener('click', () => {
  handleEditAvatarClick ();
  });

function handleEditAvatarClick (selector) {
  selector.classList.add('popup_opened');
}

function handleEditProfileClick (selector) {
  document.querySelector('profile__button-edit').classList.add('popup_opened');
}

function handleAddPlaceClick (selector) {
  document.querySelector('profile__button-add').classList.add('popup_opened');
}

  return(
    <main>
            <section className="profile">
                <div className="profile__avatar-container">
                    <img className="profile__avatar" src="<%=require('./images/avatar.png')%>" alt="Фото профиля" />
                </div>
                <div className="profile__name">
                    <div className="profile__intro">
                        <h1 className="profile__intro-title">Жак-Ив Кусто</h1>
                        <button className="profile__button-edit" type="button"></button>
                    </div>
                    <p className="profile__intro-subtitle">Исследователь океана</p>
                </div>

                <button className="profile__button-add"type="button"></button>
            </section>

            <section className="elements">

            </section>
        </main>
  )
}

export default Main;
