import React from "react";
import {Navigate, BrowserRouter, Route, Routes} from "react-router-dom"; 
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import api from "../utils/Api";
import useAuth from "../utils/auth";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import {CurrentCard} from "../contexts/CurrentCard";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import Login from "./Login";
import Register from "./Register";
import ProtectedRoute from "./ProtectedRoute";
// import InfoTooltip from "./InfoTooltip";
import success from "../images/success.svg";
import noSuccess from "../images/noSuccess.svg"


function App() {
  const [isEditAvatarClick, setEditAvatarClick] = React.useState(false);
  const [isEditProfileClick, setEditProfileClick] = React.useState(false);

  const [isAddPlaceClick, setAddPlaceClick] = React.useState(false);
  const [selectedCard, setselectedCard] = React.useState(null);

  const [isInfoTooltipOpen, setInfoTooltip] = React.useState(false)

  const [currentUser, setCurrentUser] = React.useState({});

  const [currentCards, setCurrentCards] = React.useState([]);

  const [infoToolTipData, setInfoToolTipData] = React.useState({
    title: '',
    image: '',
  })

  //const navigate = useNavigate()
  const { loginUser, registerUser, getToken, error } = useAuth()

  const [isloggedIn, setloggedIn] = React.useState(false); // статус пользователя(вошел в систему или нет)
  // const [isEmailUser, setEmailUser] = React.useState(null);

  function handleCardLike(card) {

    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    (isLiked
    ? api.deleteLikeCard(card._id)
    : api.addLikeCard(card._id))

    .then((newCard) => {
      setCurrentCards((state) => state.map((c) => (c._id === card._id ? newCard : c))
      );
    })
    .catch((err) => {
      console.log(err);
    })
}
function handleCardDelete(card) {

  api.deleteCard(card._id)
  .then((res) => {
    setCurrentCards((state) => state.filter((c) => c._id !== card._id));
    // с - объект карточки
    // state - массив карточек до удаления 
})
  .catch((err) => {
    console.log(err);
})
}

function handleUpdateUser(data) {
  api.editInfoUser(data.name, data.about)
  .then((res) => {
    setCurrentUser(res)
    closeAllPopups()
  })
  .catch((err) => {
    console.log(err);
  })
}

function handleUpdateAvatar(data) {
  api.editAvatarUser(data.avatar)
  .then((res) => {
    setCurrentUser(res)
    closeAllPopups()
  })
  .catch((err) => {
    console.log(err);
  })
}

function handleAddPlaceSubmit(data) {
  api.createInitialCards(data.name, data.link)
  .then((newCard) => {
    setCurrentCards([newCard, ...currentCards])
    closeAllPopups()
  })
  .catch((err) => {
    console.log(err);
  })
}

function onRegister(email, password) {
  useAuth.registerUser(email, password)
    .then(() => {
      setInfoToolTipData({
        image: success, //переменная к картинке
        title: 'Вы успешно зарегистрировались!',
      })
      navigate('/sign-in')
    })
    .catch(() => {
      setInfoToolTipData({
        image: noSuccess, //переменная к картинке
        title: 'Что-то пошло не так! Попробуйте ещё раз.',
      })
      console.error('Error: ' + error)
    })
    .finally(handleInfoTooltip)
}

// function onLogin(email, password) {
//   loginUser(email, password)
//     .then((res) => {
//       localStorage.setItem('jwt', res.token)
//       setloggedIn(true)
//       setEmailUser(email)
//       navigate('/')
//     })
//     .catch(() => {
//       setInfoToolTipData({
//         image: success, //переменная к картинке
//         title: 'Что-то пошло не так! Попробуйте ещё раз.',
//       })
//       console.error('Error: ' + error)
//       handleInfoTooltip()
//     })
// }

// React.useEffect(() => {
//   if (isLoggedIn === true) {
//     navigate('/')
//   }
// }, [isLoggedIn, navigate])

  React.useEffect(() => {
    //if (isLoggedIn) 
    Promise.all([api.getInfoUser(), api.getInitialCards()])
      .then((res) => {
        setCurrentUser(res[0]);
        setCurrentCards([...res[1]]);
      })

      .catch((err) => {
        console.log(err);
      });
  }, []);

  const closeAllPopups = () => {
    setEditAvatarClick(false);
    setEditProfileClick(false);
    setAddPlaceClick(false);
    setselectedCard(null);
    // isInfoTooltipOpen && setInfoTooltip(false);
  };

  const handleEditAvatarClick = () => {
    return setEditAvatarClick(!isEditAvatarClick);
  };

  const handleEditProfileClick = () => {
    return setEditProfileClick(!isEditProfileClick);
  };

  const handleAddPlaceClick = () => {
    return setAddPlaceClick(!isAddPlaceClick);
  };

  const handleCardClick = (card) => {
    setselectedCard(card);
  };

  const handleInfoTooltip = () => {
    setInfoTooltip(true)
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CurrentCard.Provider value={currentCards}>
      <BrowserRouter>

      <div className="page">

        <Routes>

        <Route
            path="/sign-in"
            element={
              <>
                <Header title="Регистрация" route="/sign-up" />
                {/* <Login onLogin={onLogin} /> */}
              </>
            }
          />
          <Route
            path="/sign-up"
            element={
              <>
                <Header title="Войти" route="/sign-in" />
                <Register onRegister={onRegister} />
              </>
            }
          />

           <Route
            exact
            path="/"
            element={
              <>
                <Header
                  title="Выйти"
                  // email={emailName}
                  // onClick={onSignOut}
                  route=""
                />
                <ProtectedRoute
                  component={Main}
                  isLogged={isloggedIn}
                  onEditAvatar={handleEditAvatarClick}
                  onEditProfile={handleEditProfileClick}
                  onAddPlace={handleAddPlaceClick}
                  onCardClick={handleCardClick}
                  cards={currentCards}
                  onCardLike={handleCardLike}
                  onCardDelete={handleCardDelete}
                />
                <Footer />
              </>
            }
          />

          <Route
            path="*"
            element={<Navigate to={isloggedIn ? '/' : '/sign-in'} />}
          />

      </Routes>

      <EditProfilePopup 
      isOpen={isEditProfileClick} 
      onClose={closeAllPopups} 
      onUpdateUser={handleUpdateUser}/>

      <AddPlacePopup 
      isOpen={isAddPlaceClick} 
      onClose={closeAllPopups} 
      onUpdatePlace={handleAddPlaceSubmit}/>

      <EditAvatarPopup 
      isOpen={isEditAvatarClick} 
      onClose={closeAllPopups} 
      onUpdateAvatar={handleUpdateAvatar}/>

      <ImagePopup 
      card={selectedCard} 
      onCardClick={handleCardClick} 
      onClose={closeAllPopups}/>

      {/* <InfoTooltip
      image={infoToolTipData.image}
      title={infoToolTipData.title} 
      isOpen={isInfoTooltipOpen}
      onClose={closeAllPopups} 
      /> */}

    </div>
    </BrowserRouter>
    </CurrentCard.Provider>
    </CurrentUserContext.Provider>
  );
}

export default App;
