import React from 'react';

function Main () {
/*
const [isEditAvatarClick, setEditAvatarClick] = useState(' ');
const [isEditProfileClick, setEditProfileClick] = useState(' ');
const [isAddPlaceClick, setAddPlaceClick] = useState(' ');
*/
function handleEditAvatarClick () {
  const popup = document.querySelector('.popup-new-avatar');
  popup.classList.add('popup_opened');

}

function handleEditProfileClick () {
  const popup = document.querySelector('.profile-popup');
  popup.classList.add('popup_opened');
}

function handleAddPlaceClick () {
  const popup = document.querySelector('.popup-add');
  popup.classList.add('popup_opened');
}

  return(
    <main>
            <section className="profile">
                <div className="profile__avatar-container" onClick={handleEditAvatarClick}>
                    <img className="profile__avatar" src="<%=require('./images/avatar.png')%>" alt="Фото профиля" />
                </div>
                <div className="profile__name">
                    <div className="profile__intro">
                        <h1 className="profile__intro-title">Жак-Ив Кусто</h1>
                        <button className="profile__button-edit" type="button" onClick={handleEditProfileClick}></button>
                    </div>
                    <p className="profile__intro-subtitle">Исследователь океана</p>
                </div>

                <button className="profile__button-add"type="button" onClick={handleAddPlaceClick}></button>
            </section>

            <section className="elements">

            </section>
        </main>
  )
}

export default Main;
