import React from 'react';
import Header from './Header'
import Main from './Main'
import Footer from './Footer'
import PopupWithForm from './PopupWithForm'
import ImagePopup from './ImagePopup'

function App() {

    const [isEditAvatarClick, setEditAvatarClick] = React.useState(false);
    const [isEditProfileClick, setEditProfileClick] = React.useState('');
    const [isAddPlaceClick, setAddPlaceClick] = React.useState(false);

    const handleEditAvatarClick = () => {
      return setEditAvatarClick(!isEditAvatarClick)
    }

    const handleEditProfileClick = () => {
      return setEditProfileClick('popup_opened')
    }

    const handleAddPlaceClick = () => {
      return setAddPlaceClick(!isAddPlaceClick)
    }


  return (
    <div className="page">
       <Header/>
        <Main 
        onEditProfile = {handleEditProfileClick} 
        onAddPlace = {handleAddPlaceClick}
        onEditAvatar = {handleEditAvatarClick}/>
        <Footer/>

        <PopupWithForm 
        name="profile-popup" 
        title="Редактировать профиль"
        isOpen={handleEditProfileClick}>
        <input className="popup__input popup__name-text popup__input_type_name" id="popup__name" type="text" defaultValue={''} value="" placeholder="Имя" name="name" minlength="2" maxlength="40" required/>
        <span className="popup__input-error popup__name-error"></span>
        <input className="popup__input popup__name-text popup__input_type_job" id="popup__text" type="text" defaultValue={''} placeholder="О себе" name="about" minlength="2" maxlength="200" required/>
        <span className="popup__input-error popup__text-error"></span>
        </PopupWithForm>

        <PopupWithForm 
        name="popup-add" 
        title="Новое место">
        <input className="popup__input popup__name-text popup__input_type_title" id="popup__title" type="text" placeholder="Название" name="name" defaultValue={''} minlength="2" maxlength="30" required/>
        <span className="popup__input-error popup__title-error"></span>
        <input className="popup__input popup__name-text popup__input_type_link" id="popup__link" type="url" placeholder="Ссылка на картинку" name="link" defaultValue={''} required/>
        <span className="popup__input-error popup__link-error"></span>

        </PopupWithForm>

        <PopupWithForm 
        name="popup-new-avatar" 
        title="Обновить аватар">
        <input className="popup__input popup__name-text popup__input_type_avatar" id="popup__newAvatar" type="url" name="link" defaultValue={''} required/>
        <span className="popup__input-error popup__newAvatar-error"></span>
        </PopupWithForm>

        <ImagePopup/>

        <template id="cards">
            <div className="element">
                <img src="#" className="element__image" alt="" />
                <div className="element__item">
                    <p className="element__title" name="name"></p>
                    <button className="element__delete"></button>
                    <div className="element__likes-and-number">
                        <button className="element__like"></button>
                        <span className="element__like-number"></span>
                    </div>
                </div>
            </div>
        </template>
      </div>
  );
}

export default App;
