import React from "react";

function EditProfilePopup(isOpen, onClose) {

  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");

  // Обработчик изменения инпута обновляет стейт
  function handleChangeName(e) {
    setName(e.target.value);
  }

  return(
    <>
    <div name="profile-popup" title="Редактировать профиль" isOpen={isOpen} onClose={onClose} textButton="Cохранить">
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_name" id="popup__name" type="text" defaultValue={name} placeholder="Имя" name="name" minLength="2" maxLength="40" required />
          <span className="popup__input-error popup__name-error"></span>
        </div>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_job" id="popup__text" type="text" defaultValue={description} placeholder="О себе" name="about" minLength="2" maxLength="200" required />
          <span className="popup__input-error popup__text-error"></span>
        </div>
      </div>
    </>
  )
}

export default EditProfilePopup;
