import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import api from "../utils/Api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import {CurrentCard} from "../contexts/CurrentCard";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";

function App() {
  const [isEditAvatarClick, setEditAvatarClick] = React.useState(false);
  const [isEditProfileClick, setEditProfileClick] = React.useState(false);
  const [isAddPlaceClick, setAddPlaceClick] = React.useState(false);
  const [selectedCard, setselectedCard] = React.useState(null);

  const [currentUser, setCurrentUser] = React.useState({});

  const [currentCard, setCurrentCard] = React.useState([]);

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    (isLiked
    ? api.deleteLikeCard(card._id)
    : api.addLikeCard(card._id))

    .then((newCard) => {
      setCurrentCard((state) => state.map((c) => (c._id === card._id ? newCard : c))
      );
    })
    .catch((err) => {
      console.log(err);
    })
}
function handleCardDelete(card) {

  api.deleteCard(`/cards/${card._id}`)
  .then((cards) => {
    setCurrentCard((state) => state.filter((c) => c._id === card._id ? cards : c));
    // с - объект карточки
    // state - массив карточек до удаления 
})
  .catch((err) => {
    console.log(err);
})
}

function handleUpdateUser(data) {
  api.editInfoUser(data.name, data.about)
  .then((res) => {
    setCurrentUser(res)
    closeAllPopups()
  })
  .catch((err) => {
    console.log(err);
  })
}

function handleUpdateAvatar(data) {
  api.editAvatarUser(data.avatar)
  .then((res) => {
    setCurrentUser(res)
    closeAllPopups()
  })
  .catch((err) => {
    console.log(err);
  })
}

function handleAddPlaceSubmit(data) {
  api.createInitialCards(data.name, data.link)
  .then((newCard) => {
    setCurrentCard([newCard, ...currentCard])
    closeAllPopups()
  })
  .catch((err) => {
    console.log(err);
  })
}

  React.useEffect(() => {
    Promise.all([api.getInfoUser(), api.getInitialCards()])
      .then((res) => {
        setCurrentUser(res[0]);
        setCurrentCard([...res[1]]);
      })

      .catch((err) => {
        console.log(err);
      });
  }, []);

  const closeAllPopups = () => {
    setEditAvatarClick(false);
    setEditProfileClick(false);
    setAddPlaceClick(false);
    setselectedCard(null);
  };

  const handleEditAvatarClick = () => {
    return setEditAvatarClick(!isEditAvatarClick);
  };

  const handleEditProfileClick = () => {
    return setEditProfileClick(!isEditProfileClick);
  };

  const handleAddPlaceClick = () => {
    return setAddPlaceClick(!isAddPlaceClick);
  };

  const handleCardClick = (card) => {
    setselectedCard(card);
  };

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CurrentCard.Provider value={currentCard}>
    <div className="page">
      <Header />
      <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick} onCardClick={handleCardClick} onCardLike={handleCardLike} onCardDelete={handleCardDelete}/>
      <Footer />

      <EditProfilePopup isOpen={isEditProfileClick} onClose={closeAllPopups} onUpdateUser={handleUpdateUser}/>

      <AddPlacePopup isOpen={isAddPlaceClick} onClose={closeAllPopups} onUpdatePlace={handleAddPlaceSubmit}></AddPlacePopup>

      <EditAvatarPopup isOpen={isEditAvatarClick} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar}></EditAvatarPopup>

      <ImagePopup card={selectedCard} onCardClick={handleCardClick} onClose={closeAllPopups} />
    </div>
    </CurrentCard.Provider>
    </CurrentUserContext.Provider>
  );
}

export default App;
