import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import api from "../utils/Api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import {CurrentCard} from "../contexts/CurrentCard";

function App() {
  const [isEditAvatarClick, setEditAvatarClick] = React.useState(false);
  const [isEditProfileClick, setEditProfileClick] = React.useState(false);
  const [isAddPlaceClick, setAddPlaceClick] = React.useState(false);
  const [selectedCard, setselectedCard] = React.useState(null);

  const [currentUser, setCurrentUser] = React.useState({});

  const [currentCard, setCurrentCard] = React.useState([]);

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    (isLiked
    ? api.deleteLikeCard(card._id)
    : api.addLikeCard(card._id))

    .then((newCard) => {
      setCurrentCard((state) => state.map((c) => (c._id === card._id ? newCard : c))
      );
    })
    .catch((err) => {
      console.log(err);
    })
}
function handleCardDelete(card) {
  const isOwn = card.owner._id === currentUser._id;

  api.deleteCard(card._id, !isOwn).then((cards) => {
    setCurrentCard((state) => state.filter((c) => c._id === cards._id ? cards : c));
    // с - объект карточки
    // state - массив карточек до удаления 
});

}

  React.useEffect(() => {
    Promise.all([api.getInfoUser(), api.getInitialCards()])
      .then((res) => {
        setCurrentUser(res[0]);
        setCurrentCard([...res[1]]);
      })

      .catch((err) => {
        console.log(err);
      });
  }, []);

  const closeAllPopups = () => {
    setEditAvatarClick(false);
    setEditProfileClick(false);
    setAddPlaceClick(false);
    setselectedCard(null);
  };

  const handleEditAvatarClick = () => {
    return setEditAvatarClick(!isEditAvatarClick);
  };

  const handleEditProfileClick = () => {
    return setEditProfileClick(!isEditProfileClick);
  };

  const handleAddPlaceClick = () => {
    return setAddPlaceClick(!isAddPlaceClick);
  };

  const handleCardClick = (card) => {
    setselectedCard(card);
  };

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CurrentCard.Provider value={currentCard}>
    <div className="page">
      <Header />
      <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick} onCardClick={handleCardClick} onCardLike={handleCardLike} onCardDelete={handleCardDelete}/>
      <Footer />

      <EditProfilePopup />

      <ImagePopup card={selectedCard} onCardClick={handleCardClick} onClose={closeAllPopups} />
    </div>
    </CurrentCard.Provider>
    </CurrentUserContext.Provider>
  );
}

export default App;
