import React from "react";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
//import {CurrentCard} from "../contexts/CurrentCard";
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup(props) {

  const [title, setTitle] = React.useState("");
  const [place, setPlace] = React.useState("");

  const currentUser = React.useContext(CurrentUserContext);

  React.useEffect(() => {
    if (currentUser) {
      setTitle(currentUser.name);
      setPlace(currentUser.place);
    }
}, [currentUser]); 

  // Обработчик изменения инпута обновляет стейт
  function handleChangeName(e) {
    setName(e.target.value);
  }

  function handleChangePlace(e) {
    setPlace(e.target.value);
  }

  function handleSubmit(e) {
    // Запрещаем браузеру переходить по адресу формы
    e.preventDefault();

    // Передаём значения управляемых компонентов во внешний обработчик
    props.onUpdatePlace({
      name,
      place
    });
  }

  // function onAddPlace() {

  // }

  return(
    <PopupWithForm name="popup-add" title="Новое место" textButton="Создать" isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit}>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_title" id="popup__title" type="text" placeholder="Название" name="name" value={name} onChange={handleChangeName} minLength="2" maxLength="30" required />
          <span className="popup__input-error popup__title-error"></span>
        </div>
        <div className="popup__input-position">
          <input className="popup__input popup__name-text popup__input_type_link" id="popup__link" type="url" placeholder="Ссылка на картинку" name="link" value={place} onChange={handleChangePlace} required />
          <span className="popup__input-error popup__link-error"></span>
        </div>
      </PopupWithForm>
  )
}

export default AddPlacePopup;
