import React from "react";
import api from "../utils/Api";
import Card from "./Card";

function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick }) {
  const [userName, setUserName] = React.useState();
  const [userDescription, setUserDescription] = React.useState();
  const [userAvatar, setUserAvatar] = React.useState();

  const [cards, setCards] = React.useState([]);

  // let userId = null;
  React.useEffect(() => {
    Promise.all([api.getInfoUser(), api.getInitialCards()])
      .then((res) => {
        // setUserName(res[0].name);
        // setUserDescription(res[0].about);
        // setUserAvatar(res[0].avatar);
        setCards([...res[1]]);
      })

      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <>
      <main>
        <section className="profile">
          <div className="profile__avatar-container" onClick={onEditAvatar}>
          <img class="profile__avatar" src={userAvatar} alt="Фото профиля" />
          </div>
          <div className="profile__name">
            <div className="profile__intro">
              <h1 className="profile__intro-title">{userName}</h1> 
              <button className="profile__button-edit" type="button" onClick={onEditProfile}></button>
            </div>
            <p className="profile__intro-subtitle">{userDescription}</p>
          </div>

          <button className="profile__button-add" type="button" onClick={onAddPlace}></button>
        </section>
      </main>

      <section className="elements">
        {cards.map((card) => {
          return <Card key={card._id} card={card} link={card.link} name={card.name} like={card.likes.length} onCardClick={onCardClick} />;
        })}
      </section>
    </>
  );
}

export default Main;
